import React, {useState} from "react";

export default function Contact(){
  const [tel, setTel] = useState("");
  const submit = (e) => {
    e.preventDefault();
    alert(`Спасибо! Мы перезвоним на номер: ${tel}`);
    setTel("");
  };

  return (
    <section id="contact" className="section" style={{paddingTop:16}}>
      <div className="container">
        <h2 style={{textAlign:"left"}}>Связаться с нами</h2>
        <p style={{marginTop:-8, color:"var(--muted)"}}>
          Готовы сделать первый шаг? Оставьте номер телефона — наш консультант свяжется с вами.
        </p>
        <form onSubmit={submit} style={{display:"flex", gap:12, maxWidth:520}}>
          <input
            className="input"
            type="tel"
            placeholder="Телефон *"
            value={tel}
            onChange={(e)=>setTel(e.target.value)}
            required
          />
          <button className="btn" type="submit">Отправить</button>
        </form>
      </div>
    </section>
  );
}
