import React from "react";

const bg =
  "https://images.unsplash.com/photo-1519681393784-d120267933ba?q=80&w=1600&auto=format&fit=crop"; // заглушка

export default function Hero(){
  return (
    <section id="top" style={{position:"relative"}}>
      <div style={{
        position:"relative", height:520, display:"grid", placeItems:"center",
        color:"#fff", textAlign:"left",
        backgroundImage:`linear-gradient(0deg,rgba(0,0,0,.45),rgba(0,0,0,.45)), url(${bg})`,
        backgroundSize:"cover", backgroundPosition:"center"
      }}>
        <div className="container" style={{display:"grid", gap:18, maxWidth:900}}>
          <h1 style={{fontSize:64, lineHeight:1, margin:0, fontWeight:700}}>
            Завод вентиляторов
          </h1>
          <div style={{
            display:"grid", gap:14, gridTemplateColumns:"1fr", color:"#eaeaea", fontSize:18
          }}>
            <p style={{margin:0, maxWidth:720}}>
              ООО «Вентагрегат» — производитель промышленных вентиляторов.
              Выпускаем оборудование для вентиляции любых объектов.
            </p>
            <ul style={{margin:"6px 0 0 18px"}}>
              <li>Свое оборудование из отечественного сырья</li>
              <li>Строгий контроль качества</li>
              <li>Доставка по всей России</li>
            </ul>
          </div>
        </div>
      </div>
    </section>
  );
}
